function wppfm_arrayObjectIndexOf(theArray,searchTerm,arrayProperty){for(var i=0,len=theArray.length;i<len;i++){if(theArray[i][arrayProperty]===searchTerm){return i}}return-1}function wppfm_getUrlVariable(key){var result="";var url=window.location.search.substring(1);var params=url.split("&");for(var i=0;i<params.length;i++){var param=params[i].split("=");if(param[0]===key){result=param[1]}}return result}function wppfm_countObjectItems(object){var count=0;for(var k in object){if(object.hasOwnProperty(k)){count++}}return count}function wppfm_isEmptyQueryObject(object){if(object===null){return true}if(object.length>0){return false}if(object.length===0){return true}for(var key in object){if(object.hasOwnProperty(key)){return false}}return true}function wppfm_incrementLast(stringWithNumber){return stringWithNumber.replace(/[0-9]+(?!.*[0-9])/,parseInt(stringWithNumber.match(/[0-9]+(?!.*[0-9])/),10)+1)}